/* Minimal Preline-like interaction helpers (offline shim). This is not the real Preline. */
(function(){
  function ready(fn){if(document.readyState!=='loading'){fn();}else{document.addEventListener('DOMContentLoaded',fn);} }
  ready(function(){
    // data-hs-dropdown: toggle next .dropdown-content
    document.body.addEventListener('click',function(e){
      var btn=e.target.closest('[data-hs-dropdown]');
      if(!btn) return;
      var menu=btn.nextElementSibling; if(!menu||!menu.classList) return;
      if(menu.classList.contains('hidden')) menu.classList.remove('hidden'); else menu.classList.add('hidden');
    });
    // data-hs-accordion: toggle aria-expanded and hidden on target
    document.body.addEventListener('click',function(e){
      var t=e.target.closest('[data-hs-accordion-toggle]');
      if(!t) return; var id=t.getAttribute('aria-controls'); var panel=id&&document.getElementById(id);
      if(!panel) return; var expanded=t.getAttribute('aria-expanded')==='true';
      t.setAttribute('aria-expanded', String(!expanded)); panel.hidden = expanded;
    });
  });
})();
